$(document).keydown(function (cordin) {
  if (cordin.keyCode == 123) {
    return false;
  } else {
    if (cordin.ctrlKey && cordin.shiftKey && cordin.keyCode == 73) {
      return false;
    } else {
      if (cordin.ctrlKey && (cordin.keyCode === 85 || cordin.keyCode === 117)) {
        return false;
      } else {
        return true;
      }
    }
  }
});
